function idade(ano, mes, dia) {
  var d = new Date(),
    anoAtual = d.getFullYear(),
    mesAtual = d.getMonth() + 1,
    diaAtual = d.getDay(),
    ano = +ano,
    mes = +mes,
    dia = +dia,
    qtsAnos = anoAtual - ano;

  if (mesAtual < mes || (mesAtual === mes && diaAtual < dia)) {
    qtsAnos--;
  }

  return qtsAnos < 0 ? 0 : qtsAnos;
}

