var skillBody = $("#basic");
Skill.forEach(item => {
  var template = `
    <div class="${item.skillName}">
        <svg viewBox="0 0 24 24"><path d="${item.skillPath}"></path></svg>
        <span class="caption">${item.skillName}</span>
        <div class="thermometer"></div>
    </div>`;
  skillBody.append(template);
  for (let i = 0; i < 5; i++) {
    var templatePoints = `<span style="border-color: ${item.color}"></span>`;
    if (i < item.points) {
      templatePoints = `<span style="border-color: ${item.color}; background-color: ${item.color}"></span>`;
    }
    skillBody
      .find("." + item.skillName + " .thermometer")
      .append(templatePoints);
  }
  var skillElement = skillBody.find("." + item.skillName)[0];
  skillElement.addEventListener("mouseover", ev => {
    skillBody.find("." + item.skillName + " svg").css("fill", item.color);
  });
  skillElement.addEventListener("mouseleave", ev => {
    skillBody.find("." + item.skillName + " svg").css("fill", "#000");
  });
});

